import React ,{Component} from 'react';
import Person from '../person/Person'
import App from '../App.css'


class OutputtingLists extends Component
{
    state={
        persons:[
        {name:"vaishnavi", age:21},
        {name:"madhvi", age:23},
        {name:"sonu", age:21},
        {name:"bhagya", age:22}
        ]
    }

    buttonClickHandler=()=> {
        //console.log("button clicked");
       // console.log(this.state.persons[0].name);
       // console.log(this.state.persons[0].age);

       this.setState({
         persons:[
         {name:"vaishu", age:21},
         {name:"madhvi", age:23},
         {name:"sonu", age:21},
         {name:"bhagya", age:22}
       ]

       });
     }



    render()
    {
        return(
            <div className="App">
                <button onClick={this.buttonClickHandler}>click Me </button>

                <Person name={this.state.persons[0].name}/>
            </div>
        );
    }
} 

export default OutputtingLists;
